import torch
from transformers import BertTokenizer, BertModel
from sklearn.metrics.pairwise import cosine_similarity
import numpy as np

# 模型路径
model_path = "/Users/MacBook/PycharmProjects/BERT/bert_lcr_model_LeCARD.pth"

# 定义模型类
class BertForLCR(torch.nn.Module):
    def __init__(self):
        super(BertForLCR, self).__init__()
        self.bert = BertModel.from_pretrained("bert-base-chinese")
        self.dropout = torch.nn.Dropout(0.3)
        self.classifier = torch.nn.Linear(self.bert.config.hidden_size, 4)  # 4分类

    def forward(self, input_ids, attention_mask):
        outputs = self.bert(input_ids=input_ids, attention_mask=attention_mask)
        pooled_output = outputs.pooler_output
        pooled_output = self.dropout(pooled_output)
        logits = self.classifier(pooled_output)
        return pooled_output, logits  # 返回语义向量和分类结果

# 加载模型
device = torch.device("cuda" if torch.cuda.is_available() else "cpu")
model = BertForLCR()
model.load_state_dict(torch.load(model_path, map_location=device))
model.to(device)
model.eval()

# 加载分词器
tokenizer = BertTokenizer.from_pretrained("bert-base-chinese")

# 案件向量化函数（同时获取分类结果）
def encode_case_with_classification(case_text):
    encoding = tokenizer(
        case_text,
        max_length=512,
        padding="max_length",
        truncation=True,
        return_tensors="pt",
    )
    input_ids = encoding["input_ids"].to(device)
    attention_mask = encoding["attention_mask"].to(device)

    with torch.no_grad():
        pooled_output, logits = model(input_ids=input_ids, attention_mask=attention_mask)
        pooled_output = pooled_output.cpu().numpy()
        logits = logits.cpu().numpy()

    return pooled_output, logits

# 检索函数（结合分类结果）
def retrieve_similar_cases_with_classification(query_case, candidate_cases, k):
    # 对查询案件进行向量化和分类
    query_vector, query_logits = encode_case_with_classification(query_case)
    query_class = np.argmax(query_logits)  # 查询案件的分类结果

    # 对候选案件进行向量化和分类
    candidate_vectors = []
    candidate_classes = []
    for case in candidate_cases:
        vec, logits = encode_case_with_classification(case)
        candidate_vectors.append(vec)
        candidate_classes.append(np.argmax(logits))  # 候选案件分类结果

    # 计算余弦相似度
    similarities = [
        cosine_similarity(query_vector, vec)[0][0] for vec in candidate_vectors
    ]

    # 加权处理：类别相同的案件相似度加权提升
    weighted_similarities = [
        sim * (1.5 if candidate_classes[i] == query_class else 1.0)
        for i, sim in enumerate(similarities)
    ]

    # 排序并选出前 K 个
    top_k_indices = np.argsort(weighted_similarities)[-k:][::-1]
    top_k_cases = [
        (candidate_cases[i], weighted_similarities[i]) for i in top_k_indices
    ]

    return top_k_cases

